typedef struct {
  PangoAttribute attr;
  gboolean embossed;
} GdkPangoAttrEmbossed;

typedef struct {
  PangoAttribute attr;
  PangoColor color;
} GdkPangoAttrEmbossColor;

typedef struct {
  PangoAttribute attr;
  GdkBitmap *stipple;
} GdkPangoAttrStipple;
