typedef struct {
  gchar *target;
  guint  flags;
  guint  info;
} GtkTargetEntry;

typedef struct {
  GList *list;
  guint ref_count;
} GtkTargetList;

typedef struct {
  GdkAtom   target;
  guint     flags;
  guint     info;
} GtkTargetPair;

